// Generated by CoffeeScript 2.3.1
(function() {
  $(function() {
    var socket;
    // learn socket.io -- lesson2:websocket send,broadcast.
    socket = io();
    socket.on('message', function(msg) {
      return $('#messages').append('<div class="well">' + (msg.replace('\n', '<br>')) + '</div>');
    });
    return $('form').on('submit', function(evt) {
      var message;
      evt.preventDefault();
      message = $('#message').val();
      $('#message').val('');
      return socket.send(message);
    });
  });

}).call(this);
