// Generated by CoffeeScript 2.3.2
(function() {
  $(function() {
    var format, obj2list, populate, prefill;
    // defines a inner help function 
    prefill = function(num) {
      var str;
      str = '' + num;
      if (str.length === 1) {
        return '0' + str;
      }
      return str;
    };
    format = function(value) {
      var date, day, hour, milliseconds, minute, month, seconds, year;
      date = new Date(value);
      year = date.getFullYear();
      month = prefill(date.getMonth() + 1); // Jan is '0' in Date API
      day = prefill(date.getDate());
      hour = prefill(date.getHours());
      minute = prefill(date.getMinutes());
      seconds = prefill(date.getSeconds());
      milliseconds = date.getMilliseconds();
      return year + '-' + month + '-' + day + 'T' + hour + ':' + minute + ':' + seconds + '.' + milliseconds + 'Z';
    };
    $('table.table').on('click', 'button.detailbutton', function(evt) {
      var alias, browser, category, id, utc_ms;
      evt.preventDefault();
      evt.stopPropagation();
      alias = $(this).data('detail-alias');
      id = $(this).data('detail-id');
      category = $(this).data('detail-category');
      browser = $(this).data('detail-browser');
      utc_ms = $(this).data('detail-utc-ms');
      return alert(['条目编号: ' + id, '用户: ' + alias, '类别: ' + category, '浏览器描述: ' + browser, '打卡时间（科学表示毫秒）: ' + utc_ms].join('\n'));
    });
    $('form').on('submit', function(e) {
      // ajax do query
      $.ajax({
        url: '/daka/one',
        data: $(this).serialize(),
        type: 'GET',
        dataType: 'json'
      }).done(function(json) {
        var $tbody;
        $tbody = $('.table tbody');
        $tbody.html('');
        return populate($tbody, json);
      });
      e.preventDefault();
      return e.stopPropagation();
    });
    
    // populate() is a help func.
    populate = function(tbody, arr) {
      var $button, $tr, ele, i, len, results;
// arr -> [{},{},...] ,each {} -> {alias:xxx,utc_ms:xxx,....}
// change each element to '<tr>'
// change each attr of element to '<td>'
      results = [];
      for (i = 0, len = arr.length; i < len; i++) {
        ele = arr[i];
        $tr = $('<tr/>');
        
        // order:['alias','category','timestamp','dakaer']
        $tr.append($('<td/>', {
          text: ele.alias
        }));
        $tr.append($('<td/>', {
          text: ele.category
        }));
        //$tr.append $('<td/>',{text:(new Date(ele.utc_ms)).toLocaleString()})
        $tr.append($('<td/>', {
          text: format(ele.utc_ms)
        }));
        $tr.append($('<td/>', {
          text: ele.dakaer
        }));
        $button = $('<button/>', {
          text: 'Detail',
          'class': "btn btn-default detailbutton",
          'data-detail-id': ele.id,
          'data-detail-browser': ele.browser,
          'data-detail-alias': ele.alias,
          'data-detail-utc-ms': ele.utc_ms,
          'data-detail-dakaer': ele.dakaer,
          'data-detail-category': ele.category
        });
        $tr.append($('<td/>').append($button));
        results.push(tbody.append($tr));
      }
      return results;
    };
    // help function - obj2list
    return obj2list = function(obj) {
      var $list;
      $list = $('<ol/>');
      $list.append($('<li/>').html('<strong>id:</strong>' + obj.id));
      $list.append($('<li/>').html('<strong>user:</strong>' + obj.alias));
      $list.append($('<li/>').html('<strong>browser:</strong>' + obj.browser));
      return $list;
    };
  });

}).call(this);
